 holder.ivTaskCheckbox.setImageResource(R.drawable.ic_checkbox_checked);
 holder.ivTaskCheckbox.setBackgroundResource(R.drawable.bg_checkbox_circle_checked);
 holder.ivTaskCheckbox.setAlpha(1.0f);

