 member.setName(doc.getString("name"));
 member.setEmail(doc.getString("email"));
 member.setRole(doc.getString("role"));
 member.setFamilyId(doc.getString("familyId"));
 member.setProfileImageUrl(doc.getString("profileImageUrl"));
